('SampleCodeBean', '9155668f-bb8c-4efd-8735-5ad9a698f85d', 'class Enemy extends Phaser.Physics.Arcade.Sprite {\r\n  constructor(scene, x, y, left, right) {\r\n    super(scene, x, y, \"enemy\");\r\n    this.scene = scene;\r\n    scene.physics.world.enable(this);\r\n    scene.add.existing(this);\r\n    this.setCollideWorldBounds(true);\r\n    this.direction = -1;\r\n    this.lastDownTime = new Date().getTime();\r\n    this.left = left;\r\n    this.right = right;\r\n  }\r\n\r\n  update() {\r\n    //敵人的動畫 - 3\r\n\r\n    ///////////////////\r\n    if (this.direction == -1) {\r\n      if (this.x <= this.left) {\r\n        this.direction = 1;\r\n      }\r\n    } else {\r\n      if (this.x >= this.right) {\r\n        this.direction = -1;\r\n      }\r\n    }\r\n    this.setVelocityX(this.direction * 50);\r\n    let now = new Date().getTime();\r\n    if (now - this.lastDownTime > 5000) {\r\n      this.y += 10;\r\n      this.lastDownTime = now;\r\n      if (Math.random() > 0.8 && this.body.enable) {\r\n        let bomb = new Bomb(this.scene, this.x, this.y);\r\n        this.scene.bombs.add(bomb);\r\n        bomb.update();\r\n      }\r\n    }\r\n  }\r\n}', 'Enemy.js', NULL, NULL),
