('CodeInfoBean', 'faa39003-d1bc-4ade-a81c-2060dcf478ce', '<html>\r\n  <head>\r\n    <script src=\"https://cdn.jsdelivr.net/npm/phaser@3.15.1/dist/phaser-arcade-physics.min.js\"></script>\r\n    <script src=\"./Scene.js\"></script>\r\n    <script src=\"./Rocket.js\"></script>\r\n    <script src=\"./Enemy.js\"></script>\r\n    <script src=\"./Bullet.js\"></script>\r\n    <script src=\"./Bomb.js\"></script>\r\n    <script src=\"./Block.js\"></script>\r\n  </head>\r\n\r\n  <body>\r\n    <script>\r\n      var config = {\r\n        type: Phaser.AUTO,\r\n        width: 800,\r\n        height: 600,\r\n        physics: {\r\n          default: \"arcade\",\r\n          arcade: {\r\n            debug: false\r\n          }\r\n        },\r\n        scene: new Scene()\r\n      };\r\n      var game = new Phaser.Game(config);\r\n    </script>\r\n  </body>\r\n</html>', 'index.html', 0, '9dee6f94-1c82-41b5-89e7-0bdc26928757'),
('CodeInfoBean', 'fba7f903-317c-4d81-8539-9b00c1cf35d0', 'class Block extends Phaser.Physics.Arcade.Sprite {\r\n  constructor(scene, x, y) {\r\n    super(scene, x, y, \"block\");\r\n    this.scene = scene;\r\n    scene.physics.world.enable(this);\r\n    scene.add.existing(this);\r\n  }\r\n}', 'Block.js', 0, '832dd4ff-8c7f-4bbc-b132-6248798433e7'),
('CodeInfoBean', 'fc8dbed8-b36d-41ef-ab74-ea55bb73ede2', 'class Enemy extends Phaser.Physics.Arcade.Sprite {\r\n  constructor(scene, x, y, left, right) {\r\n    super(scene, x, y, \"enemy\");\r\n    this.scene = scene;\r\n    scene.physics.world.enable(this);\r\n    scene.add.existing(this);\r\n    this.setCollideWorldBounds(true);\r\n    this.direction = -1;\r\n    this.lastDownTime = new Date().getTime();\r\n    this.left = left;\r\n    this.right = right;\r\n  }\r\n\r\n  update() {\r\n    //敵人的動畫 - 3\r\n\r\n    ///////////////////\r\n    if (this.direction == -1) {\r\n      if (this.x <= this.left) {\r\n        this.direction = 1;\r\n      }\r\n    } else {\r\n      if (this.x >= this.right) {\r\n        this.direction = -1;\r\n      }\r\n    }\r\n    this.setVelocityX(this.direction * 50);\r\n    let now = new Date().getTime();\r\n    if (now - this.lastDownTime > 5000) {\r\n      this.y += 10;\r\n      this.lastDownTime = now;\r\n      if (Math.random() > 0.8 && this.body.enable) {\r\n        let bomb = new Bomb(this.scene, this.x, this.y);\r\n        this.scene.bombs.add(bomb);\r\n        bomb.update();\r\n      }\r\n    }\r\n  }\r\n}', 'Enemy.js', 0, 'd0b463a9-46e9-433d-8693-8f8105e0d074'),
